import Firebase from "../../firebase_settings/index.js"
import { doc, getDoc } from "firebase/firestore"
//, setDoc, serverTimestamp 

export default {
    receive_data(uid) {
        const db = Firebase.db;
        db.collection('userData').where('id', '==', Number(this.text)).get(
        ).then((querySnapshot) => {
            querySnapshot.forEach((doc) => {
                // テーブル表示
                this.items = [doc.data()];
            });
        }).catch((error) => {
            console.log(error);
            // テーブルリセット
            this.items = [];
        });
    },
}
